function DevToolsGetFormatConfig(grRecord)
{
	// TODO: Adapt this code to support your tables or just return false...

	var strTable = grRecord.getRecordClassName();

	if (strTable == 'x_your_table')
	{
		var formatconfig = {
			performance: {
				decimals: 2,
				postfix: ' %'
			}
		};
		return formatconfig;
	}

	return false;
}
