function DevToolsGetInstancePipelineName(strInstanceName)
{
	// TODO: Create the specific mapping of instance names to pipeline instance names...

	switch (strInstanceName)
	{
		case 'acme':
			return 'prod';
		case 'acmetest':
			return 'test';
		case 'acmedev':
			return 'dev';
		default:
	}

	return '';
}
