sys_script_include.script:
function DevToolsGetApplicationStatus(status)
{
	// TODO: Adapt this code to add more columns to the DevTools application overview page (or just return status)...

	var GetValue = x_snc_devtools.GetValue;
	var SetValue = x_snc_devtools.SetValue;
	var GetKeys = x_snc_devtools.GetKeys;
	var GetAppRecord = x_snc_devtools.GetAppRecord;

	// Define a new column 'demo'
	SetValue('KoliBri',status,'columns','demo','header');

	var aApps = GetKeys(GetValue(status,'applications'));
	for (var nApp = 0; nApp < aApps.length; nApp++)
	{
		var grApp = GetAppRecord(aApps[nApp]);
		// TODO: Add some meaningful status information or useful links for each app:
		SetValue('TODO: Add some relevant info about app '+grApp.name,status,'applications',aApps[nApp],'demo');
	}

	return status;
}
