sys_ui_script.script:
class KoliBriDemoApp extends ReactorComponent
{
	constructor(props)
	{
		super(props);
		var strTheme = GetStringValue(this.props.theme);
		if (strTheme == '')
		{
			strTheme = 'bmf';
		}
		this.state = {
			state: 'start',
			theme: strTheme
		};
		//BindAllFunctionsToThis(this);
		this.onClickThemeDE = this.onClickThemeDE.bind(this);
		this.onClickThemeITZBUND = this.onClickThemeITZBUND.bind(this);
		this.onClickThemeBMF = this.onClickThemeBMF.bind(this);
		this.onClickThemeECLEC = this.onClickThemeECLEC.bind(this);
		this.onClickThemeECLEU = this.onClickThemeECLEU.bind(this);

	}

	componentDidMount()
	{
	}

	componentWillUnmount()
	{
	}

	onClickThemeDE()
	{
		this.setState( {theme: 'de', state: 'changetheme' } );
	}
	onClickThemeITZBUND()
	{
		this.setState( {theme: 'itzbund', state: 'changetheme' } );
	}
	onClickThemeBMF()
	{
		this.setState( { theme: 'bmf', state: 'changetheme' } );
	}	
	onClickThemeECLEC()
	{
		this.setState( { theme: 'ecl-ec', state: 'changetheme' } );
	}	
	onClickThemeECLEU()
	{
		this.setState( { theme: 'ecl-eu', state: 'changetheme' } );
	}	

	stateStart()
	{
		return (
			<div data-theme={ this.state.theme }>
				<kol-heading _level="1" _label="KoliBri Demo App"></kol-heading>
				<kol-button _label="BMF" _value="itzbund" onClick={ this.onClickThemeBMF }/>
				<kol-button _label="DE" _value="itzbund" onClick={ this.onClickThemeDE }/>
				<kol-button _label="ITZBund" _value="itzbund" onClick={ this.onClickThemeITZBUND }/>
				<kol-button _label="ECL_EC" _value="ecl-ec" onClick={ this.onClickThemeECLEC }/>
				<kol-button _label="ECL_EU" _value="ecl-eu" onClick={ this.onClickThemeECLEU }/>

				<kol-heading _level="2" _label="Alert"></kol-heading>
				<kol-alert _label="Überschrift im Alert" _level="1" _type="success" _variant="msg">Textbereich im Alert</kol-alert>
				<kol-alert _label="Überschrift im Alert" _level="2" _type="info" _variant="card">Textbereich im Alert</kol-alert>					

				<kol-heading _level="2" _label="Breadcrump"></kol-heading>
				<kol-breadcrumb _links='[{"_label":"Startseite","_href":"#/"},{"_label":"Unterseite von Startseite","_href":"#/unterseite"}]'></kol-breadcrumb>
				<kol-heading _level="2" _label="Button"></kol-heading>
				<kol-button _label="Test" onClick={ this.onClick }/>
				<kol-heading _level="2" _label="Spin"></kol-heading>
				<kol-spin _show/>


				<kol-kolibri></kol-kolibri>

			</div>
		);	
	}

	stateChangeTheme()
	{
		this.setState( { state: 'start' } );
		return e(Centered,{},[
			e('kol-spin',{ _show: true },null)]);
	}

	render()
	{

		if (this.state.state == 'changetheme')
		{
			return this.stateChangeTheme();
		}

		return this.stateStart();


		/*
		var themeOptions = [
			{'label':'ITZBund','value':'0'},
			{'label':'BMF','value':'1'},
			{'label':'ECL-EC','value':'2'}
			];
			*/

		//var strOptions = JSON.stringify(themeOptions);

		/*

		_rows="4"
						_multiple
		*/

	/*
						<kol-select
						_options="[{'label':'ITZBund','value':'0'},{'label':'BMF','value':'1'},{'label':'ECL-EC','value':'2'}]"
						_value="['0']"
						>
						Theme
					</kol-select>
	*/

	}	
}
